import React from 'react';
import Sidebar from './Sidebar';

interface ChatPageProps {
  users: any[];
  selectedUser: string | null;
  onUserSelect: (uid: string) => void;
}

const ChatPage: React.FC<ChatPageProps> = ({ users, selectedUser, onUserSelect }) => {
  return (
    <div style={{ display: 'flex' }}>
      {/* Pass the users and onUserSelect function to Sidebar */}
      <Sidebar users={users} onUserSelect={onUserSelect} />

      <div style={chatAreaStyle}>
        {selectedUser ? (
          <div>
            <h3>Chat with {selectedUser}</h3>
            {/* You can now display the chat for the selected user here */}
          </div>
        ) : (
          <p>Please select a user to start chatting.</p>
        )}
      </div>
    </div>
  );
};

const chatAreaStyle = {
  marginLeft: '250px',  // Adjust for Sidebar width
  padding: '20px',
  width: '100%',
  backgroundColor: '#f9f9f9',
};

export default ChatPage;
