import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { getToken } from 'firebase/messaging';  // Import getToken from Firebase Messaging
import { messaging } from '../firebaseConfig';  // Import your Firebase messaging instance

const Register = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    // Request notification permission when the component mounts
    const requestNotificationPermission = async () => {
      try {
        const permission = await Notification.requestPermission();
        if (permission !== 'granted') {
          setError('Notification permission is required for registration.');
        }
      } catch (error) {
        console.error('Error requesting notification permission:', error);
        setError('Failed to request notification permission.');
      }
    };

    requestNotificationPermission();
  }, []);

  // Get the FCM Token
  const getFCMToken = async () => {
    try {
      const token = await getToken(messaging, {
        vapidKey: 'BFD2u_zBUQGOkcAGHujWEwvZ_4Z2ooOX7qAlijVA9H755geE0MY645XZvf5SALjVOkWWAz30awa8XdW4S_cjjWM-ceYgmm3s6dgeFfclBDCqSC0hQgdZCsjkcnPXEYnvVE', // Replace with your VAPID key
      });
      if (!token) {
        console.error('FCM Token is null or undefined');
        return null;
      }
      console.log('FCM Token:', token);
      return token;
    } catch (error) {
      console.error('Error getting FCM token:', error);
      return null;
    }
  };

  const handleRegister = async (e: React.FormEvent) => {
    e.preventDefault();
    // const fcmToken = await getFCMToken();  // Get FCM token before sending the request

    // if (!fcmToken) {
    //   setError('FCM Token is required for registration');
    //   return;
    // }

    try {
      // Send the registration request to the backend API with FCM token
      const response = await axios.post('http://localhost:4000/register', {
        email,
        password,
        fcmToken,  // Send the fcmToken
      });
      console.log(response);

      // Assuming the backend returns a success message, redirect to chat
      if (response.status === 201) {
        navigate('/chat');  // Redirect to chat page after successful registration
      }
    } catch (error: any) {
      console.log("network error========>", error.response?.data?.message || error.message);
      setError(error.response?.data?.message || error.message);  // Set error if registration fails
    }
  };

  return (
    <div className="form-container">
      <h2>Register</h2>
      <form onSubmit={handleRegister}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        {error && <div>{error}</div>}  {/* Display error message if there's one */}
        <button type="submit">Register</button>
      </form>
    </div>
  );
};

export default Register;
