// src/pages/Chat.tsx
import React, { useEffect, useState } from 'react';
import { db, auth, storage } from '../firebaseConfig';
import { collection, query, onSnapshot, setDoc, doc } from 'firebase/firestore';
import { getAuth } from 'firebase/auth';
import { ref, uploadBytes } from 'firebase/storage';
import ChatPage from '../components/MainSidebar';

const Chat = () => {
  const [messages, setMessages] = useState<any[]>([]);
  const [messageInput, setMessageInput] = useState('');
  const [users, setUsers] = useState<any[]>([]);
  const [selectedUser, setSelectedUser] = useState<string | null>(null);

  useEffect(() => {
    // Fetch users from Firestore
    const usersQuery = query(collection(db, 'users'));
    onSnapshot(usersQuery, (snapshot) => {
      setUsers(snapshot.docs.map(doc => doc.data()));
    });

    // Real-time message updates
    const messagesQuery = query(collection(db, 'messages'));
    onSnapshot(messagesQuery, (snapshot) => {
      setMessages(snapshot.docs.map(doc => doc.data()));
    });

    // Check for user status (online/offline)
    const authStateChange = getAuth().onAuthStateChanged((user) => {
      if (user) {
        setDoc(doc(db, 'users', user.uid), { status: 'online' });
      }
    });

    return () => authStateChange();
  }, []);

  const sendMessage = async () => {
    if (messageInput.trim()) {
      await setDoc(doc(db, 'messages', new Date().toISOString()), {
        message: messageInput,
        sender: auth.currentUser?.email,
        recipient: selectedUser,
        timestamp: new Date().toISOString(),
      });
      setMessageInput('');
    }
  };

  const uploadFile = async (file: File) => {
    const storageRef = ref(storage, `chat-files/${file.name}`);
    await uploadBytes(storageRef, file);
    console.log('File uploaded successfully');
  };

  // Filter messages between current user and the selected user
  const filteredMessages = messages.filter(
    (msg) =>
      (msg.sender === auth.currentUser?.email && msg.recipient === selectedUser) ||
      (msg.sender === selectedUser && msg.recipient === auth.currentUser?.email)
  );

  return (
    <div style={{ display: 'flex' }}>
      <Sidebar users={users} onUserSelect={setSelectedUser} />

      <div style={chatAreaStyle}>
        {selectedUser ? (
          <>
            <h3>Chat with {selectedUser}</h3>
            <div style={messagesContainerStyle}>
              {filteredMessages.map((msg, index) => (
                <div
                  key={index}
                  style={{
                    textAlign: msg.sender === auth.currentUser?.email ? 'right' : 'left',
                    padding: '5px 10px',
                    backgroundColor: msg.sender === auth.currentUser?.email ? '#007bff' : '#f1f1f1',
                    color: msg.sender === auth.currentUser?.email ? 'white' : 'black',
                    margin: '5px 0',
                    borderRadius: '10px',
                    maxWidth: '70%',
                    marginLeft: msg.sender === auth.currentUser?.email ? 'auto' : '0',
                  }}
                >
                  <strong>{msg.sender}: </strong>{msg.message}
                </div>
              ))}
            </div>

            <textarea
              value={messageInput}
              onChange={(e) => setMessageInput(e.target.value)}
              placeholder="Type a message"
              style={textareaStyle}
            />
            <button onClick={sendMessage} style={sendButtonStyle}>Send</button>
            <input type="file" onChange={(e) => e.target.files && uploadFile(e.target.files[0])} style={fileInputStyle} />
          </>
        ) : (
          <div>Select a user to start chatting.</div>
        )}
      </div>
    </div>
  );
};

const chatAreaStyle = {
  marginLeft: '250px', // To make space for the sidebar
  padding: '20px',
  width: 'calc(100% - 250px)',
  backgroundColor: '#f4f4f4',
};

const messagesContainerStyle: React.CSSProperties = {
  height: '400px',
  overflowY: 'scroll',
  marginBottom: '10px',
  padding: '10px',
  backgroundColor: '#ffffff',
  borderRadius: '10px',
};

const textareaStyle = {
  width: '100%',
  padding: '10px',
  marginBottom: '10px',
  borderRadius: '5px',
  border: '1px solid #ddd',
  fontSize: '14px',
};

const sendButtonStyle = {
  padding: '10px 15px',
  backgroundColor: '#007bff',
  color: 'white',
  border: 'none',
  borderRadius: '5px',
  cursor: 'pointer',
};

const fileInputStyle = {
  marginTop: '10px',
};

export default Chat;
