import React from 'react';
import { User } from '../Chat'; // Import User type from Chat

interface SidebarProps {
  users: User[];
  onUserSelect: (uid: string | null) => void;
}

const Sidebar: React.FC<SidebarProps> = ({ users, onUserSelect }) => {
  return (
    <div style={sidebarStyle}>
      <h2>Users</h2>
      <ul style={userListStyle}>
        {users.map(user => (
          <li
            key={user.uid}
            onClick={() => onUserSelect(user.uid)}
            style={userItemStyle}
          >
            <span>{user.email}</span> - <span>{user.status}</span> {/* Display status */}
          </li>
        ))}
      </ul>
    </div>
  );
};

const sidebarStyle: React.CSSProperties = {
  width: '250px',
  padding: '20px',
  backgroundColor: '#333',
  color: 'white',
  height: '100vh',
  overflowY: 'auto',
};

const userListStyle: React.CSSProperties = {
  listStyleType: 'none',
  padding: '0',
};

const userItemStyle: React.CSSProperties = {
  padding: '10px',
  cursor: 'pointer',
  borderBottom: '1px solid #444',
};

export default Sidebar;
