// components/Sidebar.tsx
import React from 'react';

interface User {
  uid: string;
  email: string;
  status: string;
}

interface SidebarProps {
  users: User[];
  onUserSelect: (uid: string) => void;  // Correct type for the callback function
}

const Sidebar: React.FC<SidebarProps> = ({ users, onUserSelect }) => {
  return (
    <div style={sidebarStyle}>
      <h3>Users</h3>
      {users.map((user) => (
        <button onClick={() => onUserSelect(user.uid)} style={buttonStyle}>
            <div key={user.uid} style={userStyle}>
                <span>{user.email}</span>
                {user.status === 'online' ? (
                <span style={{ color: 'green' }}> ●</span>
                ) : (
                <span style={{ color: 'red' }}> ●</span>
                )}

            </div>
        </button>
      ))}
    </div>
  );
};

const sidebarStyle = {
  width: '250px',
  height: '100vh',
  backgroundColor: '#f4f4f4',
  padding: '20px',
  boxShadow: '2px 0 5px rgba(0, 0, 0, 0.1)',
  position: 'fixed' as 'fixed',
  left: '0',
};

const userStyle = {
  marginBottom: '15px',
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
};

const buttonStyle = {
  padding: '5px 10px',
  backgroundColor: '#007bff',
  color: 'white',
  border: 'none',
  cursor: 'pointer',
};

export default Sidebar;
