// src/components/Sidebar.tsx
import React from 'react';

interface SidebarProps {
  users: any[];
  onUserSelect: (uid: string) => void;
}

const Sidebar: React.FC<SidebarProps> = ({ users, onUserSelect }) => {
  return (
    <div style={sidebarStyle}>
      <h3>Users</h3>
      {users.map((user) => (
        <div key={user.uid} style={userStyle}>
          <span>{user.email}</span>
          {user.status === 'online' ? (
            <span style={{ color: 'green' }}> ●</span>
          ) : (
            <span style={{ color: 'red' }}> ●</span>
          )}
          <button onClick={() => onUserSelect(user.uid)} style={buttonStyle}>
            Chat
          </button>
        </div>
      ))}
    </div>
  );
};

// Static users data
const staticUsers = [
  { uid: '1', email: 'user1@example.com', status: 'online' },
  { uid: '2', email: 'user2@example.com', status: 'offline' },
  { uid: '3', email: 'user3@example.com', status: 'online' },
  { uid: '4', email: 'user4@example.com', status: 'offline' },
  { uid: '5', email: 'user5@example.com', status: 'online' },
  { uid: '6', email: 'user6@example.com', status: 'offline' },
  { uid: '7', email: 'user7@example.com', status: 'online' },
  { uid: '8', email: 'user8@example.com', status: 'offline' },
  { uid: '9', email: 'user9@example.com', status: 'online' },
  { uid: '10', email: 'user10@example.com', status: 'offline' },
];

// Render the Sidebar component with static data
const SidebarWrapper = () => {
  const handleUserSelect = (uid: string) => {
    console.log(`User selected: ${uid}`);
  };

  return <Sidebar users={staticUsers} onUserSelect={handleUserSelect} />;
};

const sidebarStyle = {
  width: '250px',
  height: '100vh',
  backgroundColor: '#f4f4f4',
  padding: '20px',
  boxShadow: '2px 0 5px rgba(0, 0, 0, 0.1)',
  position: 'fixed' as 'fixed',
  left: '0',
};

const userStyle = {
  marginBottom: '15px',
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
};

const buttonStyle = {
  padding: '5px 10px',
  backgroundColor: '#007bff',
  color: 'white',
  border: 'none',
  cursor: 'pointer',
};

export default SidebarWrapper;
